mapper.select:
SELECT i_rest, nm, lat, lng
FROM t_restaurant		
WHERE lat BETWEEN #{sw_lat} AND #{ne_lat}
AND lng BETWEEN #{sw_lng} AND #{ne_lng};

SELECT A.i_rest, A.nm, A.lat, A.lng, A.addr, A.i_user, A.hits , ifnull(B.val, '') as cd_category_nm , C.nm as user_nm , ifnull(D.cnt, 0) as cnt_favorite
FROM t_restaurant A LEFT JOIN c_code_d B ON A.cd_category = B.cd AND B.i_m = 1
LEFT JOIN t_user C ON A.i_user = C.i_user
LEFT JOIN ( SELECT i_rest, COUNT(i_rest) as cnt FROM t_user_favorite WHERE i_rest = #{i_rest} GROUP BY i_rest
) D ON A.i_rest = D.i_rest WHERE A.i_rest = #{i_rest}

SELECT A.seq, A.menu_nm, A.menu_price, A.menu_pic
FROM t_restaurant_recommend_menu A

	INNER JOIN t_restaurant B
	ON A.i_rest = B.i_rest
	AND B.i_user = #{i_user}

WHERE i_rest = #{i_rest}

	AND A.seq = #{seq}

mapper.delete:
DELETE A FROM t_restaurant_recommend_menu A
INNER JOIN t_restaurant B
ON A.i_rest = B.i_rest
WHERE B.i_rest = #{i_rest}
AND B.i_user = #{i_user}	

	AND A.seq = #{seq}

DELETE A FROM t_restaurant_menu A
INNER JOIN t_restaurant B
ON A.i_rest = B.i_rest
WHERE B.i_rest = #{i_rest}
AND B.i_user = #{i_user}		

	AND A.seq = #{seq}

DELETE FROM t_restaurant
WHERE i_rest = #{i_rest}
AND i_user = #{i_user}

mapper.insert:
INSERT INTO t_restaurant_recommend_menu
(i_rest, seq, menu_nm, menu_price, menu_pic)
SELECT #{i_rest}, ifnull(max(seq), 0) + 1, #{menu_nm}, #{menu_price}, #{menu_pic}
FROM t_restaurant_recommend_menu
WHERE i_rest = #{i_rest}
